const sleep=t=>new Promise((e=>setTimeout(e,1e3*t)));function get_title(){return document.getElementById("main_title").innerHTML}function set_title(t){document.getElementById("main_title").innerHTML=t}async function blink(t=5,e=.4){for(let i=0;i<t;i+=1)await sleep(e),set_title(i%2==0?get_title().slice(0,-1)+" ":get_title().slice(0,-1)+"|")}async function typer(t){const e=get_title().length;for(let t=0;t<e;t+=1)await sleep(.1),set_title(get_title().slice(0,-2)+"|");const i=t.length;for(let e=0;e<i;e+=1)await sleep(.1),set_title(get_title().slice(0,-1)+(t[e]+"|"));await blink()}async function main(){for(await blink(8);;)await typer("Design • Invent • Create"),await typer("Study • Learn • Master"),await typer("Share • Inspire • Educate")}main();